current_horizontal_index = 0
current_vertical_index = 0
current_horizontal_index_robot = 0
current_vertical_index_robot = 0
current_horizontal = 0
current_vertical = 0
total_counter = 0

grid = {}

counter = 0

for direction in map:
    counter += 1

    if counter % 2 == 0:
        if direction == '^':
            current_vertical_index += 1
        elif direction == 'v':
            current_vertical_index -= 1
        elif direction == '>':
            current_horizontal_index += 1
        elif direction == '<':
            current_horizontal_index -= 1
        else :
            print( "unknown character: " + direction )

        active_horizontal = current_horizontal_index
        active_vertical = current_vertical_index
    else:
        if direction == '^':
            current_vertical_index_robot += 1
        elif direction == 'v':
            current_vertical_index_robot -= 1
        elif direction == '>':
            current_horizontal_index_robot += 1
        elif direction == '<':
            current_horizontal_index_robot -= 1
        else :
            print( "unknown character: " + direction )

        active_horizontal = current_horizontal_index_robot
        active_vertical = current_vertical_index_robot



    if not active_vertical in grid:
        grid[active_vertical] = {}
    if not active_horizontal in grid[active_vertical]:
        grid[active_vertical][active_horizontal] = 0

    grid[active_vertical][active_horizontal] += 1

for key, value in grid.iteritems():
    for horizontal in value:
        total_counter += 1

print( total_counter )
